import * as React from "react";
import Alert from "@mui/material/Alert";
import AlertTitle from "@mui/material/AlertTitle";
import Dialog from "@mui/material/Dialog";
import Button from "@mui/material/Button";
import AccessAlarmIcon from "@mui/icons-material/AccessAlarm";

export default function CustomAlert({name}) {
//alerts
  const [invalid, setInvalid] = React.useState(false);
  const [deleteLetter, setDelete] = React.useState(false);
  const [enter, setEnter] = React.useState(false);
  const [won, setWon] = React.useState(false);

  const setAlart = (name) => {
      //turn on or off dialog based on ID
    if (name==="invalid"){
        setInvalid(!invalid)
    }else if (name==="deleteLetter"){
        setDelete(!deleteLetter)
    }else if (name==="enter"){
        setEnter(!enter)
    }else if (name==="won"){
        setWon(!won)
    }
  };


  return (
    <>
      <Button onClick={handleClick("invalid")} variant="outlined">
        Open Alert!
      </Button>
      <Dialog id="invalid" open={invalid} onClose={handleClick("invalid")}>
        <Alert
          severity="warning"
          color="error"
          role="button"
          icon={<AccessAlarmIcon />}
          onClose={() => {}}
          closeText="Doesn't Work!"
          sx={{
            // width: '80%',
            // margin: 'auto',
            "& .MuiAlert-icon": {
              color: "blue"
            }
            //backgroundColor: "green"
          }}
        >
          <AlertTitle>Danger!</AlertTitle>
          The "Close" button does not work.
        </Alert>
      </Dialog>

    </>
  );
}
