import React, { useContext } from "react"
import { AppContext } from "../App";
import Alert from "@mui/material/Alert";
import AlertTitle from "@mui/material/AlertTitle";
import Dialog from "@mui/material/Dialog";
import Button from "@mui/material/Button";
import AccessAlarmIcon from "@mui/icons-material/AccessAlarm";
import DeleteAlert from "./alerts/DeleteAlert"
import EnterAlert from "./alerts/EnterAlert"
import InvalidAlert from "./alerts/invalidAlert"
import WonAlert from "./alerts/WonAlert"

export default function CustomAlert({name}) {
const{invalid,deleteLetter,enter,won} = useContext(AppContext)

  const setAlert = (name) => {
      //turn on or off dialog based on ID
    if (name==="invalid"){
        setInvalid(!invalid)
    }else if (name==="deleteLetter"){
        setDelete(!deleteLetter)
    }else if (name==="enter"){
        setEnter(!enter)
    }else if (name==="won"){
        setWon(!won)
    }
  };


  return (
    <>
      <Button onClick={setAlert("invalid")} variant="outlined">
        Open Alert!
      </Button>
      <DeleteAlert show = {deleteLetter}/>
      <EnterAlert show = {enter}/>
      <WonAlert show = {invalid}/>
      <InvalidAlert show = {won}/>
    </>
  );
}
