import React, { useContext } from "react"
import Button from '@mui/material/Button';
import { AppContext } from "../App";
import { createTheme, ThemeProvider } from '@mui/material/styles';

export default function Key({keyVal, eORd, guessed}) {
    const{onSelectLetter, onDeleteLetter, onEnterLetter, darkMode} = useContext(AppContext)
    const selectLetter = () => {
        if (keyVal === 'ENTER'){
            onEnterLetter()
        }else if (keyVal === 'DELETE'){
            onDeleteLetter()
        }else{
            onSelectLetter(keyVal)
    }
    }
    const theme = createTheme({
        palette: {
          primary: {
            main: '#9c9c9c',
            contrastText: '#fff',
          },
          secondary: {
            main: '#ffffff',
            contrastText: '#0000000',
          },
          darkSecondary: {
            main: '#000000',
            contrastText: '#fff',
          },
        },
      });
    return (
        <ThemeProvider theme={theme}>
        <Button
        variant="contained"
        color = {guessed?'primary': (darkMode?'darkSecondary':'secondary')}
        sx={{
            margin: '5px',
            fontSize: '15px',
            cursor: 'pointer',
          }} 

          onClick={selectLetter}
        >{keyVal}</Button>
        </ThemeProvider>

    )
}
