import React, { useContext } from "react"
import { AppContext } from "../App";
import Alert from "@mui/material/Alert";
import AlertTitle from "@mui/material/AlertTitle";
import Dialog from "@mui/material/Dialog";
import Button from "@mui/material/Button";
import AccessAlarmIcon from "@mui/icons-material/AccessAlarm";
import DeleteAlert from "./alerts/DeleteAlert"
import EnterAlert from "./alerts/EnterAlert"
import InvalidAlert from "./alerts/invalidAlert"
import WonAlert from "./alerts/WonAlert"

export default function CustomAlert({name}) {
const{invalid,deleteLetter,enter,won,setInvalid,setDelete,setEnter, setWon} = useContext(AppContext)
console.log("invalid,deleteLetter,enter,won:"+invalid,deleteLetter,enter,won)
  const setAlert = (name) => {
      console.log('setting alert')
      //turn on or off dialog based on ID
    if (name==="invalid"){
        setInvalid(!invalid)
    }else if (name==="deleteLetter"){
        setDelete(!deleteLetter)
    }else if (name==="enter"){
        setEnter(!enter)
    }else if (name==="won"){
        setWon(!won)
    }
  };


  return (
    <>
      <Button onClick={setAlert} variant="outlined">
        Open Alert!
      </Button>

      <DeleteAlert />
      <EnterAlert show={invalid}/>
      <InvalidAlert/>
      <WonAlert/>

    </>
  );
}
